mapper.select:
SELECT str_to_date(DATE_FORMAT(time_in,'%Y%m%d'),'%Y%m%d') day,count(*) value from 
(SELECT * from park_info WHERE time_in >=  #{0} and time_in  <=  #{1}) as b  
GROUP BY day ORDER BY day;

SELECT car_num carNum,time_in timeIn,time_out timeOut,park_name parkName
FROM park_info  
WHERE time_in  >=  #{0} and time_in  <=  #{1}
